import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';
import './App.css';

function App () {
  const [socket] = useState (() => io (':8000'));
  const [message, setMessage] = useState ('');
  const [chatLog, setChatLog] = useState ([]);
  const [username, setUsername] = useState ('');
  const [userList, setUserList] = useState ([]);
  const [userJoin, setUserJoin] = useState ('');
  const [userDisconnect, setUserDisconnect] = useState ('');

  useEffect (() => {
    // Send username to server for login

    // Listen for incoming chat messages from the server
    socket.on ('chat_message', data => {
      const senderId = data.username;
      const message = data.message;

      // Use the sender's socket ID and message as needed
      console.log ('Received message from:', senderId);
      console.log ('Message:', message);

      setChatLog (prevChatLog => [...prevChatLog, data]);
      const mesgList = chatLog;
    });
    socket.on ('userList', userList => {
      setUserList (userList);
    });
    socket.on ('userJoin', username => {
      setUserJoin (username);
    });
    socket.on ('userDisconnect', username => {
      setUserDisconnect (username);
    });


    console.log ('chat log sent', mesgList);


    // Clean up the event listener when the component unmounts
    return () => {
      socket.off ('chat_message');
      socket.off ('userList');
    };
  }, []);

  const handleGetUser = () => {
    // Emit the chat message to the server
    socket.emit ('login', username);
  };

  const handleSendMessage = () => {
    // Emit the chat message to the server
    socket.emit ('chat_message', message);
    setMessage ('');
  };

  return (
    <div>
      <h1>Chat App</h1>
      <div>
        <input type="text" onChange={e => setUsername (e.target.value)} />
        <button onClick={handleGetUser}>Start chatting</button>
      </div>
      <div>
        {userJoin ? <p>{userJoin} has joined the chat</p> : null}
        {userDisconnect ? <p>{userDisconnect} has left the chat</p> : null}
        {chatLog.map ((msg, index) => (
          <div key={index}>
            <p>{msg.username} said</p>
            <p>{msg.message}</p>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={e => setMessage (e.target.value)}
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
}

export default App;
