import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';
import './App.css';

function App () {
  const [socket] = useState (() => io (':8000'));
  const [message, setMessage] = useState ('');
  const [chatLog, setChatLog] = useState ([]);
  const [username, setUsername] = useState ('');
  const [userList, setUserList] = useState([]);

  useEffect (() => {
    // Send username to server for login

    // Listen for incoming chat messages from the server
    socket.on ('chat_message', data => {
      const senderId = data.username;
      const message = data.message;

      // Use the sender's socket ID and message as needed
      console.log ('Received message from:', senderId);
      console.log ('Message:', message);

      setChatLog (prevChatLog => [...prevChatLog, data]);
    });
    socket.on('userList', userList => {
      setUserList(userList);
    });
    console.log ('chat log sent', chatLog);
    // Clean up the event listener when the component unmounts
    return () => {
      socket.off ('chat_message');
      socket.off('userList');
    };
  }, []);

  const handleGetUser = () => {
    // Emit the chat message to the server
    socket.emit ('login', username);
    // setUsername ('');

  };

  const handleSendMessage = () => {
    // Emit the chat message to the server
    socket.emit ('chat_message', message);
    setMessage ('');
  };
  const list=userList;
  console.log('list', list);
  const isElementInArray = list.includes(username);
  return (
    <div>
      <h1>Chat App</h1>
      <div>
        <input
          type="text"
          value={username}
          onChange={e => setUsername (e.target.value)}
        />
        <button onClick={handleGetUser}>Start chatting</button>
      </div>
      <div>
      {isElementInArray ? (
        <p>{username} is in the array.</p>
      ) : (
        <p>{username} has joined the chat</p>
      )}
        {chatLog.map ((msg, index) => (
          <div key={index}>
            <p>{msg.username}</p>

            <p>{msg.message}</p>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={e => setMessage (e.target.value)}
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
}

export default App;
